// Group assets by user and calculate portfolio metrics
const userPortfolios = {};

for (const item of $input.all()) {
  const asset = item.json;
  if (!asset.user_id) continue;

  if (!userPortfolios[asset.user_id]) {
    userPortfolios[asset.user_id] = {
      user_id: asset.user_id,
      assets: [],
      totalValue: 0,
      totalCost: 0,
      assetTypes: new Set()
    };
  }

  const value = (asset.quantity || 0) * (asset.currentPrice || 0);
  const cost = (asset.quantity || 0) * (asset.avgPrice || 0);

  userPortfolios[asset.user_id].assets.push(asset);
  userPortfolios[asset.user_id].totalValue += value;
  userPortfolios[asset.user_id].totalCost += cost;
  userPortfolios[asset.user_id].assetTypes.add(asset.type);
}

// Transform for LLM prompt
const results = Object.values(userPortfolios).map(p => {
  const pnl = p.totalValue - p.totalCost;
  const pnlPercent = p.totalCost > 0 ? (pnl / p.totalCost * 100) : 0;
  const diversificationScore = p.assetTypes.size;

  return {
    json: {
      user_id: p.user_id,
      portfolioSummary: `Portfolio: $${p.totalValue.toFixed(2)} | PnL: ${pnlPercent.toFixed(2)}% | ${p.assets.length} assets across ${diversificationScore} categories`,
      assets: p.assets.map(a => `${a.symbol}: ${a.quantity} @ $${a.currentPrice} (${a.change24h}% 24h)`).join('\n'),
      totalValue: p.totalValue,
      diversificationCount: diversificationScore,
      pnlPercent: pnlPercent
    }
  };
});

return results;

// Parse Gemini response and format for DB
const input = $input.item.json;
let analysis;

try {
  // Extract JSON from response text
  const responseText = input.response?.content || input.text || '';
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    analysis = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (e) {
  // Fallback defaults if parsing fails
  analysis = {
    risk_score: 50,
    diversification_status: 'Moderate',
    summary: 'Unable to generate detailed analysis. Portfolio appears stable.',
    recommendations: ['Review asset allocation', 'Consider diversification options']
  };
}

return {
  json: {
    user_id: input.user_id || $('Prepare Portfolio Data').item.json.user_id,
    summary: analysis.summary,
    risk_score: Math.min(100, Math.max(0, parseInt(analysis.risk_score) || 50)),
    diversification_status: analysis.diversification_status || 'Moderate',
    recommendations: analysis.recommendations || [],
    type: 'DAILY_BRIEF',
    created_at: new Date().toISOString()
  }
};
